import React, { Component } from 'react'

export class BindingSamp extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             message:'hi'
        }
    }
    clickHandler()
    {
        this.setState({
            message:'hello'
        },
        ()=>{
            console.log(this.state.message)
        })
    }
    
    render() {
        return (
            <div>
                <button onClick={this.clickHandler.bind(this)}>click now</button>
            </div>
        )
    }
}

export default BindingSamp
