             message:'hi'
        }
        this.clickHandler=this.clickHandler.bind(this)//m3
    }
    clickHandler()
    {
        this.setState({
            message:'hello'
        },
        ()=>{
            console.log(this.state.message)
        })
    }
    
    render() {
       // console.log(this.state.message)
        return (

            <div>
                
               {/* <button onClick={this.clickHandler.bind(this)}>click now</button> //m1
                
